-- Day 4 Part 1
test_day4part1_ex1 = TestCase (assertEqual "Failed for aa bb cc dd ee\naa bb cc dd aa\naa bb cc dd aaa\n" 2 (Day4.solvePart1 "aa bb cc dd ee\naa bb cc dd aa\naa bb cc dd aaa\n"))

test_day4part1_input = TestCase (assertEqual "Failed for input" 337 (Day4.solvePart1 day4Input))

tests = TestList [
    TestLabel "Day 1 Part 1 Example 1" test_day1part1_ex1,
    TestLabel "Day 1 Part 1 Example 2" test_day1part1_ex2,
    TestLabel "Day 1 Part 1 Example 3" test_day1part1_ex3,
    TestLabel "Day 1 Part 1 Example 4" test_day1part1_ex4,
    TestLabel "Day 1 Part 1 Input" test_day1part1_input,

    TestLabel "Day 1 Part 2 Example 1" test_day1part2_ex1,
